   357→## src/hooks/useDashboardData.ts
   358→```
   359→import { useState, useEffect, useMemo, useCallback } from 'react';
   360→import type { Unternehmensverwaltung, Schichtartenverwaltung, Schichteinteilung, Mitarbeiterverwaltung } from '@/types/app';
   361→import { LivingAppsService } from '@/services/livingAppsService';
   362→
   363→export function useDashboardData() {
   364→  const [unternehmensverwaltung, setUnternehmensverwaltung] = useState<Unternehmensverwaltung[]>([]);
   365→  const [schichtartenverwaltung, setSchichtartenverwaltung] = useState<Schichtartenverwaltung[]>([]);
   366→  const [schichteinteilung, setSchichteinteilung] = useState<Schichteinteilung[]>([]);
   367→  const [mitarbeiterverwaltung, setMitarbeiterverwaltung] = useState<Mitarbeiterverwaltung[]>([]);
   368→  const [loading, setLoading] = useState(true);
   369→  const [error, setError] = useState<Error | null>(null);
   370→
   371→  const fetchAll = useCallback(async () => {
   372→    setError(null);
   373→    try {
   374→      const [unternehmensverwaltungData, schichtartenverwaltungData, schichteinteilungData, mitarbeiterverwaltungData] = await Promise.all([
   375→        LivingAppsService.getUnternehmensverwaltung(),
   376→        LivingAppsService.getSchichtartenverwaltung(),
   377→        LivingAppsService.getSchichteinteilung(),
   378→        LivingAppsService.getMitarbeiterverwaltung(),
   379→      ]);
   380→      setUnternehmensverwaltung(unternehmensverwaltungData);
   381→      setSchichtartenverwaltung(schichtartenverwaltungData);
   382→      setSchichteinteilung(schichteinteilungData);
   383→      setMitarbeiterverwaltung(mitarbeiterverwaltungData);
   384→    } catch (err) {
   385→      setError(err instanceof Error ? err : new Error('Fehler beim Laden der Daten'));
   386→    } finally {
   387→      setLoading(false);
   388→    }
   389→  }, []);
   390→
   391→  useEffect(() => { fetchAll(); }, [fetchAll]);
   392→
   393→  const unternehmensverwaltungMap = useMemo(() => {
   394→    const m = new Map<string, Unternehmensverwaltung>();
   395→    unternehmensverwaltung.forEach(r => m.set(r.record_id, r));
   396→    return m;
   397→  }, [unternehmensverwaltung]);
   398→
   399→  const schichtartenverwaltungMap = useMemo(() => {
   400→    const m = new Map<string, Schichtartenverwaltung>();
   401→    schichtartenverwaltung.forEach(r => m.set(r.record_id, r));
   402→    return m;
   403→  }, [schichtartenverwaltung]);
   404→
   405→  const mitarbeiterverwaltungMap = useMemo(() => {
   406→    const m = new Map<string, Mitarbeiterverwaltung>();
   407→    mitarbeiterverwaltung.forEach(r => m.set(r.record_id, r));
   408→    return m;
   409→  }, [mitarbeiterverwaltung]);
   410→
   411→  return { unternehmensverwaltung, setUnternehmensverwaltung, schichtartenverwaltung, setSchichtartenverwaltung, schichteinteilung, setSchichteinteilung, mitarbeiterverwaltung, setMitarbeiterverwaltung, loading, error, fetchAll, unternehmensverwaltungMap, schichtartenverwaltungMap, mitarbeiterverwaltungMap };
   412→}
   413→```
