   427→## src/lib/enrich.ts
   428→```
   429→import type { EnrichedSchichteinteilung } from '@/types/enriched';
   430→import type { Mitarbeiterverwaltung, Schichtartenverwaltung, Schichteinteilung, Unternehmensverwaltung } from '@/types/app';
   431→import { extractRecordId } from '@/services/livingAppsService';
   432→
   433→// eslint-disable-next-line @typescript-eslint/no-explicit-any
   434→function resolveDisplay(url: string | undefined, map: Map<string, any>, ...fields: string[]): string {
   435→  if (!url) return '';
   436→  const id = extractRecordId(url);
   437→  if (!id) return '';
   438→  const r = map.get(id);
   439→  if (!r) return '';
   440→  return fields.map(f => String(r.fields[f] ?? '')).join(' ').trim();
   441→}
   442→
   443→interface SchichteinteilungMaps {
   444→  mitarbeiterverwaltungMap: Map<string, Mitarbeiterverwaltung>;
   445→  unternehmensverwaltungMap: Map<string, Unternehmensverwaltung>;
   446→  schichtartenverwaltungMap: Map<string, Schichtartenverwaltung>;
   447→}
   448→
   449→export function enrichSchichteinteilung(
   450→  schichteinteilung: Schichteinteilung[],
   451→  maps: SchichteinteilungMaps
   452→): EnrichedSchichteinteilung[] {
   453→  return schichteinteilung.map(r => ({
   454→    ...r,
   455→    zuweisung_mitarbeiterName: resolveDisplay(r.fields.zuweisung_mitarbeiter, maps.mitarbeiterverwaltungMap, 'mitarbeiter_vorname'),
   456→    zuweisung_unternehmenName: resolveDisplay(r.fields.zuweisung_unternehmen, maps.unternehmensverwaltungMap, 'unternehmen_name'),
   457→    zuweisung_schichtartName: resolveDisplay(r.fields.zuweisung_schichtart, maps.schichtartenverwaltungMap, 'schichtart_name'),
   458→  }));
   459→}
