   481→const AI_ENDPOINT = "https://my.living-apps.de/litellm/v1/chat/completions";
   482→const AI_MODEL = "default";
   483→
   484→export type ChatMessage = {
   485→  role: "system" | "user" | "assistant";
   486→  content: string | Array<{ type: string; [key: string]: unknown }>;
   487→};
   488→
   489→type CompletionOptions = {
   490→  temperature?: number;
   491→  max_tokens?: number;
   492→  top_p?: number;
   493→  stop?: string[];
   494→  response_format?: { type: string };
   495→};
   496→
   497→export async function chatCompletion(
   498→  messages: ChatMessage[],
   499→  options: CompletionOptions = {}
   500→): Promise<string> {
   501→  const res = await fetch(AI_ENDPOINT, {
   502→    method: "POST",
   503→    headers: { "Content-Type": "application/json", Accept: "application/json" },
   504→    body: JSON.stringify({ model: AI_MODEL, messages, ...options }),
   505→  });
   506→  if (!res.ok) {
   507→    const body = await res.text();
   508→    throw new Error(`AI API ${res.status}: ${body}`);
   509→  }
   510→  const data = await res.json();
   511→  return data.choices[0].message.content;
   512→}
   513→
   514→export async function safeJsonCompletion<T = unknown>(
   515→  messages: ChatMessage[],
   516→  options: CompletionOptions = {}
   517→): Promise<T> {
   518→  const raw = await chatCompletion(messages, options);
   519→  const match = raw.match(/\{[\s\S]*\}|\[[\s\S]*\]/);
   520→  if (!match) throw new Error(`Expected JSON but got: ${raw.slice(0, 200)}`);
   521→  return JSON.parse(match[0]);
   522→}
   523→
   524→export async function withRetry<T>(fn: () => Promise<T>, maxAttempts = 3): Promise<T> {
   525→  for (let attempt = 1; attempt <= maxAttempts; attempt++) {
   526→    try {
   527→      return await fn();
   528→    } catch (err) {
   529→      if (attempt === maxAttempts) throw err;
   530→      await new Promise((r) => setTimeout(r, 1000 * attempt));
   531→    }
   532→  }
   533→  throw new Error("unreachable");
   534→}
   535→
   536→// --- File encoding helpers ---
   537→
   538→export function fileToDataUri(file: File): Promise<string> {
   539→  return new Promise((resolve, reject) => {
   540→    const reader = new FileReader();
   541→    reader.onload = () => resolve(reader.result as string);
   542→    reader.onerror = () => reject(new Error("Failed to read file"));
   543→    reader.readAsDataURL(file);
   544→  });
   545→}
   546→
   547→export async function urlToDataUri(url: string): Promise<string> {
   548→  const res = await fetch(url);
   549→  const blob = await res.blob();
   550→  return new Promise((resolve, reject) => {
   551→    const reader = new FileReader();
   552→    reader.onload = () => resolve(reader.result as string);
   553→    reader.onerror = () => reject(new Error("Failed to encode file"));
   554→    reader.readAsDataURL(blob);
   555→  });
   556→}
   557→
   558→// --- High-level AI features ---
   559→
   560→export async function classify(
   561→  text: string,
   562→  categories: string[]
   563→): Promise<{ category: string; confidence: number }> {
   564→  return safeJsonCompletion([
   565→    {
   566→      role: "system",
   567→      content: [
   568→        "You are a classifier. Respond ONLY with valid JSON, nothing else.",
   569→        'Output format: {"category": "<one of the allowed categories>", "confidence": <0-1>}',
   570→        `Allowed categories: ${JSON.stringify(categories)}`,
   571→      ].join("\n"),
   572→    },
   573→    { role: "user", content: text },
   574→  ], { temperature: 0 });
   575→}
   576→
   577→export async function extract<T = Record<string, unknown>>(
   578→  text: string,
   579→  schemaDescription: string
   580→): Promise<T> {
   581→  return safeJsonCompletion([
   582→    {
   583→      role: "system",
