   585→        "You are a data extraction engine. Respond ONLY with valid JSON matching the requested schema.",
   586→        "If a field cannot be determined from the input, use null.",
   587→        `Schema:\n${schemaDescription}`,
   588→      ].join("\n"),
   589→    },
   590→    { role: "user", content: text },
   591→  ], { temperature: 0 });
   592→}
   593→
   594→export async function summarize(
   595→  text: string,
   596→  options: { maxSentences?: number; language?: string } = {}
   597→): Promise<string> {
   598→  const { maxSentences = 3, language } = options;
   599→  const instructions = [
   600→    `Summarize the following text in at most ${maxSentences} sentences.`,
   601→    "Be concise and preserve key facts.",
   602→  ];
   603→  if (language) instructions.push(`Write the summary in ${language}.`);
   604→  return chatCompletion([
   605→    { role: "system", content: instructions.join(" ") },
   606→    { role: "user", content: text },
   607→  ]);
   608→}
   609→
   610→export async function translate(
